catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	//PEGA O VALOR DO CAMPO NOME
   var movie = g_form.getValue('nome').replace(/ /g, '+');
    var ga = new GlideAjax('x_908210_raposa_0.OMDBsi');
    ga.addParam('sysparm_name', 'getMovie'); //função do Script Include
    ga.addParam('sysparm_movie', movie); //nome do filme dentro da variável movie
    ga.getXML(callBackFunction); //envia a req
}

function callBackFunction(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer"); //respota da chamada
    var result = JSON.parse(answer); //JSON P JS
	if (result.exists) { //Se existir vai limpar o form
        g_form.clearValue('nome');
        g_form.clearValue('categoria');
        g_form.clearValue('sinopse');
        g_form.clearValue('nota_imdb_critica');
        g_form.addErrorMessage('A base de dados do Raposa Aí Reviews já contempla este filme. ');
    } else if (result.error) {
        g_form.addErrorMessage('Erro ao buscar informações do filme: ' + result.error);
    } else {
        var data = JSON.parse(result.data); //Converte JSON para um objeto JavaScript 
        g_form.setValue('nome', data.Title);
        g_form.setValue('categoria', data.Genre);
        g_form.setValue('sinopse', data.Plot);
        g_form.setValue('nota_imdb_critica', data.imdbRating);
		g_form.setValue('link', data.Poster);
		g_form.setValue('img_html', data.Poster);

		var posterUrl = data.Poster.replace(/^"|"$/g, ''); 
        g_form.setValue('poster', posterUrl); 
    } 
}
