catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }
    var cs = g_form.getValue('sua_nota');
    var numberRegex = /^[0-9]+(\.[0-9]*)?$/;
    if (cs.match(numberRegex)) {
        var value = parseFloat(cs);
        if (value > 10) {
            g_form.setValue('sua_nota', '');
            g_form.showErrorBox('sua_nota', 'Por favor digite uma nota menor ou igual a 10.');
            g_form.submitted = false;
            return false;
        }
        value = Math.round(value * 1000) / 1000;
        g_form.setValue('sua_nota', value);
    } else {
        g_form.setValue('sua_nota', ''); 
		g_form.showErrorBox('sua_nota', 'Por favor digite apenas números positivos.'); 
		g_form.submitted = false; return false; } }
