sp_widget.script:
(function() {

  var tableName = 'x_908210_raposa_0_filmes';

  //GlideRecord
  var gr = new GlideRecord(tableName);
  gr.query();

  var records = [];
  while (gr.next()) {
    records.push({
      nome_do_filme : gr.getValue('nome_do_filme'),
      sinopse: gr.getValue('sinopse'),
	link: gr.getValue('link')
    });
  }

  // Envia os dados para o cliente
  data.records = records;
})();
