sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var ga = new GlideAjax('OmdbApi');
    ga.addParam('sysparm_name', 'getMoviePoster');
    ga.addParam('sysparm_movieTitle', newValue);
    ga.getXMLAnswer(function(response) {
        var posterUrl = response;
        if (posterUrl) {
            g_form.setValue('movie_poster', '<img src="' + posterUrl + '" alt="Poster do Filme">');
        } else {
            g_form.setValue('movie_poster', 'Poster nÃ£o encontrado.');
        }
    });
}
