sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var requestBody = request.body.data;
    var movieTitle = requestBody.movieTitle;
    var omdbData = getOmdbData(movieTitle);

    if (omdbData && omdbData.Response === "True") {
        var movieGR = new GlideRecord('u_movie_table');
        movieGR.initialize();
        movieGR.u_title = omdbData.Title;
        movieGR.u_year = omdbData.Year;
        movieGR.u_rating = omdbData.imdbRating;
        movieGR.u_director = omdbData.Director;
        movieGR.u_genre = omdbData.Genre;
        movieGR.insert();

        response.setBody({status: 'success', data: omdbData});
    } else {
        response.setBody({status: 'error', message: 'Movie not found'});
    }

    function getOmdbData(movieTitle) {
        var client = new sn_ws.RESTMessageV2();
        client.setHttpMethod('GET');
        client.setEndpoint('http://www.omdbapi.com/?t=' + encodeURIComponent(movieTitle) + '&apikey=your_omdb_api_key');
        var response = client.execute();
        var responseBody = response.getBody();
        var parsedResponse = JSON.parse(responseBody);
        return parsedResponse;
    }
})(request, response);
